package com.ztt.consumer.cloudconsumer;import com.ztt.consumer.cloudconsumer.interfaceprovider.ProviderClient;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.openfeign.EnableFeignClients;/** * @author ZTT */@SpringBootApplication(scanBasePackages = {"com.ztt.common", "com.ztt.consumer"})@EnableFeignClients(clients = {ProviderClient.class})public class CloudConsumerApplication {    public static void main(String[] args) {        SpringApplication.run(CloudConsumerApplication.class, args);    }}